export const getServerSideProps = async (ctx: GetServerSidePropsContext) => {
  const isAdmin = await getIsAdmin(ctx);
  if (!isAdmin) {
    return {
      redirect: { permanent: false, destination: "/" },
    };
  }
  const users = await getUsers(ctx);
  return {
    props: { users },
  };
};
